mapper.select:
select
message_id, message_commodity_id, message_user_id, message_parent_id, message_content, message_create_time
from db_yiyi.message


        and message_id = #{messageId}


        and message_commodity_id = #{messageCommodityId}


        and message_user_id = #{messageUserId}


        and message_parent_id = #{messageParentId}


        and message_content = #{messageContent}


        and message_create_time = #{messageCreateTime}
